    }

    return(

        <form onSubmit = {handleSubmit}>
            <input 
        value={input}
        onChange={e => setInput(e.target.value)}
        placeholder="Set the input"
        type="text"
      />
      <button type = "submit">submit</button>
        </form>

    )


}

export default Forms
